if ( ! class_exists( 'Dq_Admin_Page_SVG_Icons' ) ) {
	/**
	 * SVG图标类
	 * 检索指定图标的SVG代码
	 */
	class Dq_Admin_Page_SVG_Icons {
		// 获取指定图标的SVG代码
		public static function get_svg( $icon, $group = 'ui', $color = '#1A1A1B' ) {

			$arr = self::$ui_icons;

			if ( array_key_exists( $icon, $arr ) ) {
				$repl = '<svg class="svg-icon" aria-hidden="true" role="img" focusable="false" ';
				$svg  = preg_replace( '/^<svg /', $repl, trim( $arr[ $icon ] ) ); // 向SVG代码添加额外的属性
				$svg  = str_replace( '#1A1A1B', $color, $svg ); // 更改颜色
				$svg  = str_replace( '#', '#', $svg ); // 编码
				$svg  = preg_replace( "/([\n\t]+)/", ' ', $svg ); // 删除换行符和选项卡
				$svg  = preg_replace( '/>\s*</', '><', $svg ); // 删除SVG标签之间的空白
				return $svg;
			}
			return null;
		}


